SELECT timestamp_measurement.UNIX_TIME, timestamp_measurement.current_hour, timestamp_measurement.current_minute, timestamp_measurement.current_second, timestamp_measurement.current_day, timestamp_measurement.current_year, node.NODE_ID, node.location, sample.SAMPLE_NO, sample.measured_variable, sample.value, sample.unit, sample.resolution FROM timestamp_measurement LEFT JOIN node ON node.UNIX_TIME = timestamp_measurement.UNIX_TIME LEFT JOIN sample ON sample.SAMPLE_NO = node.SAMPLE_NO;
